import axios from "axios";
import React from "react";
import { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import AddMeal from "../components/AddMealDay";
import binIcon from "../images/icons8-waste-50.png";
import editIcon from "../images/icons8-edit-row-50.png";
import "../css/styles.css";
import { AuthContext } from "./../context/auth.context";
import breakfastIcon from "../images/Breakfast.png";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5005";

export default function MealPlan() {
  const [meals, setMeals] = useState([]);
  const [show, setShow] = useState(false);
  const [days, setDays] = useState([
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
    "Sunday",
  ]);

  // const [monday, setMonday] = useState("");
  // const [tuesday, setTuesday] = useState("");
  // const [wednesday, setWednesday] = useState("");
  // const [thursday, setThursday] = useState("");
  // const [friday, setFriday] = useState("");
  // const [saturday, setSaturday] = useState("");
  // const [sunday, setSunday] = useState("");

  const storedToken = localStorage.getItem("authToken");

  const { user } = useContext(AuthContext);

  const toggleShow = () => {
    setShow(!show);
  };

  const getMeals = () => {
    axios
      .get(`${API_URL}/api/meals`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => {
        setMeals(response.data);
        console.log("meals: ", response.data);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getMeals();
  }, []);

  useEffect(() => {
    if (meals) {
      const updatedDays = [...days];
      meals.forEach((meal) => {
        const index = updatedDays.findIndex((day) => day === meal.day);
        if (index !== -1) {
          updatedDays.splice(index, 1, {
            day: meal.day,
            breakfast: meal.breakfast,
            lunch: meal.lunch,
            dinner: meal.dinner,
            id: meal._id,
          });
        }
      });
      console.log(days);
      setDays(updatedDays);
    }
  }, [meals]);

  const handleDelete = (mealId) => {
    axios
      .delete(`${API_URL}/api/meals/${mealId}`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then(() => {
        console.log("Item was deleted!");
        // Remove the deleted meal from the state
        setMeals((prevMeals) =>
          prevMeals.filter((meal) => meal._id !== mealId)
        );
      })
      .catch((err) => console.log("This is a delete error: ", err));

    setMeals((prevMeals) => prevMeals.filter((meal) => meal._id !== mealId));
  };

  return (
    <div>
      <div className="mealplanHeading">
        <div className="welcomeWrapper">
          {" "}
          <img src={breakfastIcon} alt="" />
          {user && (
            <h1>
              Welcome <span>{user.name}</span>!
            </h1>
          )}
        </div>
        <p>Create your new mealplan and have a nice week!</p>
        <div className="addAndDelete">
          <button className="mealButton" onClick={toggleShow}>
            {show ? "Hide Form" : "Add Meals"}
          </button>
        </div>
      </div>
      {show && (
        <AddMeal getMeals={getMeals} meals={meals} toggleShow={toggleShow} />
      )}
      <div className="mealplan">
        {meals &&
          days.map((day) => {
  const matchingMeal = meals.find((meal) => meal.day === day);

  return (
    <div className="dayColumn" key={day}>
      <p>{day}</p>
      {matchingMeal ? (
        <div className="mealsContainer">
          <div className="mealDiv">
            <a href={`/recipes/${matchingMeal.breakfast._id}`}>
              {matchingMeal.breakfast.name}
            </a>
            <img src={matchingMeal.breakfast.imageUrl} alt="" />
          </div>
          <div className="mealDiv">
            <a href={`/recipes/${matchingMeal.lunch._id}`}>
              {matchingMeal.lunch.name}
            </a>
            <img src={matchingMeal.lunch.imageUrl} alt="" />
          </div>
          <div className="mealDiv">
            <a href={`/recipes/${matchingMeal.dinner._id}`}>
              {matchingMeal.dinner.name}
            </a>
            <img src={matchingMeal.dinner.imageUrl} alt="" />
          </div>
          <div className="buttonsContainer">
            <Link
              to={`/meals/edit/${matchingMeal.id}`}
              className="editButton"
            >
              <img src={editIcon} alt="" />
            </Link>
            <button
              type="button"
              onClick={() => handleDelete(matchingMeal.id)}
              className="editButton"
            >
              <img src={binIcon} alt="" />
            </button>
          </div>
        </div>
      ) : (
        <div className="mealsContainer">
          <div className="noMealMessage">
            <span>Please add a new meal!</span>
          </div>
        </div>
      )}
    </div>
  );
})}
      </div>
    </div>
  );
}
