  // const [friday, setFriday] = useState("");
  // const [saturday, setSaturday] = useState("");
  // const [sunday, setSunday] = useState("");

  const storedToken = localStorage.getItem("authToken");

  const { user } = useContext(AuthContext);

  const toggleShow = () => {
    setShow(!show);
  };

  const getMeals = () => {
    axios
      .get(`${API_URL}/api/meals`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => {
        setMeals(response.data);
         console.log("meals: ", response.data)
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getMeals();
  }, []);

  useEffect(() => {
    if (meals) {
      const updatedDays = [...days];
      meals.forEach((meal) => {
        const index = updatedDays.findIndex((day) => day === meal.day);
        if (index !== -1) {
          updatedDays.splice(index, 1, { day: meal.day, breakfast: meal.breakfast, lunch: meal.lunch, dinner: meal.dinner, id: meal._id});
        }
      });
      console.log(days)
      setDays(updatedDays);
    }
  }, [meals]);

  // useEffect(() => {
  //   if (meals) {
  //     setMonday(meals.filter((meal) => meal.day === "Monday")[0]);
