import React from "react";
import { useEffect, useState, useContext } from "react";
import service from "../api/service";
import { AuthContext } from "./../context/auth.context";
import axios from "axios";
import recipeImage from "../images/Pizza.svg";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5005";

export default function AddRecipe({refreshRecipes, showForm}) {
  const [name, setName] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [instruction, setInstruction] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [allIngredients, setAllIngredients] = useState([]);
  const [cookingTime, setCookingTime] = useState(0);
  const [search, setSearch] = useState([]);
  const storedToken = localStorage.getItem("authToken");

  const { user } = useContext(AuthContext);
  const ingredientsCopy = [...ingredients];

  useEffect(() => {
    if (search) {
      axios
        .get(`${API_URL}/api/ingredients/search?name=${search}`, {
          headers: { Authorization: `Bearer ${storedToken}` },
        })
        .then((response) => {
          setAllIngredients(response.data);
        })
        .catch((err) => console.log("This is a search error:", err));
    }
  }, [search]);

  const handleFileUpload = (e) => {
    const uploadData = new FormData();

    uploadData.append("imageUrl", e.target.files[0]);

    service
      .uploadImage(uploadData)
      .then((response) => {
        // console.log("response is: ", response);
        // response carries "fileUrl" which we can use to update the state
        setImageUrl(response.fileUrl);
      })
      .catch((err) => console.log("Error while uploading the file: ", err));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    service
      .createRecipe({
        name,
        ingredients,
        imageUrl,
        instruction,
        cookingTime,
        userId: user._id,
      })
      .then((res) => {
        console.log("added new recipe: ", res);

        // Reset the form
        setCookingTime("");
        setImageUrl("");
        setIngredients([]);
        setInstruction("");
        setName("");

        refreshRecipes();
      })
      .catch((err) => console.log("Error while adding the new recipe: ", err));
  };

  const addIngredients = (ingredientId) => {
    console.log(ingredientsCopy);
    ingredientsCopy.splice(0, 0, ingredientId);
    setIngredients(ingredientsCopy);
  };



  return (



        <div className="form-group">
          {showForm && (
            <form onSubmit={handleSubmit} className="recipe-form">
              <div>
                <div className="addRecipeHeader">
                  <div className="burgerIconWrapper">
                    <img src={recipeImage} alt="" />
                  </div>
                  <h2>Add a Recipe</h2>
                </div>
              </div>
              <div >
                <label>Image:</label>
                <input type="file" onChange={(e) => handleFileUpload(e)} />
              </div>
              <div>
                <label
                  className="add-ingredient-label"
                  htmlFor="selectIngredients"
                >
                  {" "}
                  Ingredients:
                </label>
                <div className="ingredient-search-container">
                  <input
                    className="addIngredients  hidden"
                    id="selectIngredients"
                    type="text"
                    placeholder="Search Ingredients"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                  />

                  <div className="ingredient-list">
                    {allIngredients.map((ingredient) => {
                      return (
                        <div key={ingredient._id} className="searchDivRecipe">
                          <div className="search-recipe-P">
                            {ingredient.name}
                          </div>
                          {!ingredients.includes(ingredient._id) ? (
                            <button
                              className="searchButton"
                              type="button"
                              onClick={() => addIngredients(ingredient._id)}
                            >
                              Select
                            </button>
                          ) : (
                            <button
                              className="deselectButton"
                              type="button"
                              onClick={() => {
                                const newIngredients = ingredients.filter(
                                  (i) => i !== ingredient._id
                                );
                                console.log("deselect");
                                setIngredients(newIngredients);
                                console.log(ingredients);
                              }}
                            >
                              Deselect
                            </button>
                          )}
                        </div>
                      );
                    })}
                  </div>
                </div>
                <div>
                  <div className="ingredient-list">
                    {/* {ingredients.map((ingredient) => {
                    const ingredientName = allIngredients.find(
                      (i) => i._id === ingredient
                    )?.name;
                    return (
                      <div key={ingredient._id} className="ingredientDiv">
                        <div className="ingredientP">{ingredientName}</div>
                        <button
                          className="deselectButton"
                          type="button"
                          onClick={() => {
                            const newIngredients = ingredients.filter(
                              (i) => i !== ingredient
                            );
                            setIngredients(newIngredients);
                          }}
                        >
                          Deselect
                        </button>
                      </div>
                    );
                  })} */}
                  </div>
                </div>
              </div>

              <div>
                <label>Instruction:</label>
                <textarea
                  id="instruction"
                  name="instruction"
                  className="form-input"
                  value={instruction}
                  onChange={(e) => setInstruction(e.target.value)}
                />
              </div>
              <div >
                <label>Cooking Time:</label>
                <input
                  type="text"
                  name="cookingTime"
                  value={cookingTime}
                  onChange={(e) => setCookingTime(e.target.value)}
                />
              </div>
              <button type="submit">Submit</button>
            </form>
          )}
        </div>
  );
}
